export default {
  name: "Login",
  data: () => ({
    token: "",
    valid: false,
    email: "",
    emailRules: [
      v => !!v || "Введите почту",
      v => /.+@.+\..{2,}/.test(v) || "Неправильное значение поля"
    ],
    password: "",
    passwordRules: [v => !!v || "Введите пароль"],
    username: "",
    usernameRules: [v => !!v || "Введите имя пользователя"]
  }),
  methods: {
    submit() {
      if (this.valid) {
        let data = {
          username: this.email,
          email: this.email,
          password: this.password
        };

        this.$store
          .dispatch("login", data)
          .then(() => {
            this.$router.push("/");
          })
          .catch(error => {
            localStorage.removeItem('token');
          });
      }
    }
  }
};
